function AlertsCtrl(e){
    e.alerts=[
        {type:"success",
            msg:"Thanks for visiting! Feel free to create pull requests to improve the dashboard!"
        }
    ],
        e.addAlert=function(){e.alerts.push({msg:"Another alert!"})},e.closeAlert=function(t){e.alerts.splice(t,1)}}angular.module("RDash").controller("AlertsCtrl",["$scope",AlertsCtrl]);

function MasterCtrl(t,e){
    var o;
    o = 992;
    t.getWidth=function(){
        return window.innerWidth
    },
        t.$watch(t.getWidth,function(g){
            t.toggle=g>=o?angular.isDefined(e.get("toggle"))?e.get("toggle")?!0:!1:!0:!1}),t.toggleSidebar=function(){t.toggle=!t.toggle,e.put("toggle",t.toggle)
    },window.onresize=function(){t.$apply()}}angular.module("RDash").controller("MasterCtrl",["$scope","$cookieStore",MasterCtrl]);

angular.module("RDash").controller("LoadDashBoard",['$scope','$location',
    function LoadDashBoard($scope,$location) {
        $scope.showerror = true;
        $scope.msg = "";
        $scope.errorMsg = "";
        $scope.github_by_user = [{"id":1,"data":"ninjatrench"}];
        $scope.github_by_repo = [];
        $scope.bitbucket = [];
        $scope.bugzilla = [];
        $scope.udd = [];
        $scope.ubuntu = false
        $scope.debian = false

        $scope.updateUbuntu = function(){
            $scope.ubuntu = !$scope.ubuntu;
            alert($scope.ubuntu);

        };
        $scope.updateDebian = function(){
            $scope.debian = !$scope.debian
            alert($scope.debian);
        };

        $scope.addGithubByUser = function() {
            var newItemNo = $scope.github_by_user.length+1;
            $scope.github_by_user.push({'id':newItemNo});
        };

        $scope.removeGithubByUser = function() {
            var lastItem = $scope.github_by_user.length-1;
            $scope.github_by_user.splice(lastItem);
        };



    }]);

angular.module("RDash").controller("LoadSession",['$scope','$location',
function LoadSession($scope,$location){
    $scope.showLoadSession=true;
    $scope.showLoadedSession=false;
    $scope.sid;
    $scope.loading=false;

    var session_id = $location.search().id;

    if(session_id){
        alert(session_id);

    };

    $scope.go = function(){
        session_id = $scope.sid;
        alert($scope.sid);
        $scope.loading=true;
        $scope.showLoadedSession = false;
        $scope.showLoadSession = false;
        console.log($scope.showLoadedSession);
    };

}]);